import pandas as pd
import folium
from folium.plugins import MarkerCluster

def cars_by_country_map(df_path, JSON_path):
  df = pd.read_csv("path")

  country_counts = df['country_of_origin'].value_counts().to_dict()
  total_cars = sum(country_counts.values())
  
  # Pre-built dataset mapping countries to coordinates and flag image URLs
  country_data = {
      'South Korea': {'coordinates': (37.5665, 126.9780), 'flag_url': 'data:image/png;base64,'},
      'Japan': {'coordinates': (36.2048, 138.2529), 'flag_url': 'data:image/png;base64,'},
      'Italy': {'coordinates': (41.8719, 12.5674), 'flag_url': 'data:image/png;base64,'},
      'United States of America': {'coordinates': (37.0902, -95.7129), 'flag_url': 'data:image/png;base64,'},
      'France': {'coordinates': (46.2276, 2.2137), 'flag_url': 'data:image/png;base64," class="YQ4gaf'},
      'China': {'coordinates': (35.8617, 104.1954), 'flag_url': 'data:image/png;base64,'},
      'Germany': {'coordinates': (51.1657, 10.4515), 'flag_url': 'data:image/png;base64,'},
      'Czechia': {'coordinates': (49.8175, 15.4730), 'flag_url': 'data:image/png;base64,'},
      'Spain': {'coordinates': (40.4637, -3.7492), 'flag_url': 'data:image/png;base64,'},
      'Romania': {'coordinates': (45.9432, 24.9668), 'flag_url': 'data:image/png;base64,'},
      'India': {'coordinates': (20.5937, 78.9629),'flag_url':  'data:image/png;base64,'},
      'United Kingdom': {'coordinates': (55.3781, -3.4360), 'flag_url': 'data:image/png;base64,'},
      'Sweden': {'coordinates': (60.1282, 18.6435), 'flag_url': 'data:image/png;base64,'},
      'Russia': {'coordinates': (61.5240, 105.3188), 'flag_url': 'data:image/png;base64,'},
      'Tunisia': {'coordinates': (33.8869, 9.5375), 'flag_url': 'data:image/png;base64,'},
      'Ukraine': {'coordinates': (48.3794, 31.1656), 'flag_url': 'data:image/png;base64,'}
  }
  
  # base map with OpenStreetMap tiles
  m = folium.Map(location=[0, 0], zoom_start=2)
  
  # bins for legend
  max_count = max(country_counts.values())
  legend_bins = [x for x in range(0, max_count + 500, 500)]
  
  # Choropleth layer to color countries based on count of cars
  choropleth = folium.Choropleth(
      geo_data= JSON_path,
      name='choropleth',
      data=country_counts,
      key_on='feature.properties.name',
      fill_color='YlGnBu',
      fill_opacity=0.7,
      line_opacity=0.2,
      legend_name='Count of Cars by Country',
      bins=legend_bins,
      legend_kwds={'loc': 'bottomleft', 'title': 'Count of Cars', 'orient': 'vertical'}
  ).add_to(m)
  
  # markers for each country with the count and percentage of cars as pop-up
  marker_cluster = MarkerCluster().add_to(m)
  for country, data in country_data.items():
      count = country_counts.get(country, 0)
      percentage = (count / total_cars) * 100
      flag_url = data['flag_url']
      popup_text = (
          f'<div style="font-family: Arial; font-size: 14px; color: black; width: 200px;">'
          f'<img src="{flag_url}" style="float: right; margin-left: 5px; width: 30px; height: 20px; border: 1px solid black;">'
          f'<b>{country}</b><br>'
          f'Market Percentage: {percentage:.2f}%'
          f'</div>'
      )
      folium.Marker(
          location=data['coordinates'],
          popup=popup_text,
          icon=None
      ).add_to(marker_cluster)
  
  # Layer Control to toggle between Choropleth and MarkerCluster
  folium.LayerControl().add_to(m)
  
  m.save('cars_by_country_map.html')


df_path = r"EDA Data/EDA.csv"
JSON_path = r"EDA Data/custom.geo.json"
cars_by_country_map(df_path, JSON_path)
